import openpyxl as excel

# Функция для разбиения расписания с главного сайта на маленькие части
def data_in_excel(data):
    fl = excel.load_workbook("temp.xlsx")
    # fl = excel.Workbook()
    week_id = 1
    cur = 2
    for day in data:
        sheet = fl[f"PartWeek{week_id}"]
        # sheet = fl.active
        cur +=3
        for lesson in day[1:]:
            if lesson != []:
                cur_car = "C"
                for el in lesson[2:]:
                    sheet[cur_car+str(cur)] = el
                    cur_car = str(chr(ord(cur_car) + 1))
            cur+=1
        cur+=1
        if cur>36:
            cur=2
            week_id=2
    fl.save("rep.xlsx")

